function onlinePurchase(){listPurchase();listNotePurchase();onlinePurchaseSubmit();changeYZM()}function changeYZM(errorid,errmsg){var text=$("#changeimg");var img=$("#validateimg");text.bind("click",function(){requsetYZM(img)});img.bind("click",function(){requsetYZM(img)})}function requsetYZM(node){var urlR=G_Info.generateImage+"?v="+(new Date).getTime();node.attr("src",urlR);$("#keyRandom").val(urlR)}function showError(errorid,errmsg){$(errorid).style="float:none";$(errorid).attr("class","new_pop_error");$(errorid).prev("input").attr("class","pop_error");$(errorid).html(errmsg)}function removeError(errorid){$(errorid).removeAttr("class");$(errorid).prev("input").removeAttr("class");$(errorid).html("")}function Rcheck_null(str,msg,errorid){var str=str.replace(/\</g,"〈").replace(/\>/g,"〉").replace(/<[^>]*>/g,"");if($.trim(str)==""){showError(errorid,msg+"必填 ");return true}removeError(errorid);return false}function CheckEmail(str,errorid){var myreg=/^([a-zA-Z0-9][_\.\-]*)+\@([A-Za-z0-9])+((\.|-|_)[A-Za-z0-9]+)*((\.[A-Za-z0-9]{2,4}){1,2})$/;if(!myreg.test(str)){showError(errorid,"请输入有效的邮箱！");return true}removeError(errorid);return false}function RcheckIsIntNew(str,msg,errorid){var re=/^[0-9]*$/;if(!re.test(str)){showError(errorid,msg+"请输入数字");return true}removeError(errorid);return false}function valueTrimBr(str){var value=str.replace(/(^\s*)|(\s*$)/g,"").replace(/(^[\s\u3000]*)|([\s\u3000]*$)/g,"").replace(/\n/g," ");return value}function listPurchase(){$(".write_p").find("li").find("input").each(function(){$(this).bind("focus",function(){$(this).removeClass("pop_error");$(this).next("span").removeClass("new_pop_error");$(this).next("span").html("")})})}function listNotePurchase(){}function onlinePurchaseSubmit(){var purchaseSumbit=$("#purchase_sumbit");var onlinePurchase=$("#onlinePurchase");purchaseSumbit.bind("click",function(node){var successd=true;var companyName=valueTrimBr($("#company_name").val());var total=valueTrimBr($("#total").val());var userName=valueTrimBr($("#user_name").val());var email=valueTrimBr($("#email").val());var phone=valueTrimBr($("#phone").val());var note=valueTrimBr($("#note").val());var company_error="#"+$("#company_name").next("span").attr("id");if(Rcheck_null(companyName,"企业名称",company_error)){successd=false}var user_error="#"+$("#user_name").next("span").attr("id");if(Rcheck_null(userName,"姓名",user_error)){successd=false}var email_error="#"+$("#email").next("span").attr("id");if(Rcheck_null(email,"邮箱",email_error)){successd=false}else if(CheckEmail(email,email_error)){successd=false}var phone_error="#"+$("#phone").next("span").attr("id");var phomePat=/^[\d\-()\+]{7,20}$/;if(Rcheck_null(phone,"电话号码",phone_error)){successd=false}else if(!phomePat.test(phone)){$("#phone").addClass("pop_error");$(phone_error).html("请填写正确的电话号码").addClass("new_pop_error");successd=false}if(successd===false){return}else{$("form").submit()}})}$(function(){$(".zx_mobile_code a").mouseover(function(){$(this).next().show()});$(".zx_mobile_code a").mouseout(function(){$(this).next().hide()})});